import { useEffect, useState } from "react";
import CardAction from "@Components/card-actions";
import commonService from "@Services/commonService";
import employeeService from "@Services/employeeService";
import "@Styles/react/libs/flatpickr/flatpickr.scss";
import { selectThemeColors } from "@Utils";
import Select from "react-select";
import { SCHEMA_PRODUCT } from "@Constants/constValidate";
import { yupResolver } from "@hookform/resolvers/yup";
import { isObjEmpty } from "@Utils";
import {
  Button,
  CardBody,
  Col,
  Form,
  FormGroup,
  Input,
  InputGroup,
  Label,
  Row,
  Spinner,
} from "reactstrap";

import classnames from "classnames";
import { Image } from "react-feather";
import * as yup from "yup";

import { Controller, useFieldArray, useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";
import SizeAndAmount from "./SizeAndAmount";

const schema = yup.object(SCHEMA_PRODUCT).required();
const initialValues = {
  gender: 1,
  role: { value: "0", label: "Nhân viên", number: 2 },
  status: {
    value: "1",
    label: "Đang hoạt động",
    number: 1,
  },
  listProductDetail: [
    {
      price: 1,
      color: { value: "0", label: "Nhân viên", number: 2 },
      images: [],
      sizeAndAmount: [
        { size: { value: "0", label: "Nhân viên", number: 2 }, amount: 0 },
      ],
    },
  ],
};

const EmployeeTab = ({ selectedUser }) => {
  const [load, setLoad] = useState(true);
  const {
    register,
    handleSubmit,
    formState: { errors },
    control,
    setValue,
    getValues,
    unregister,

    reset,
  } = useForm({
    defaultValues: initialValues,

    resolver: yupResolver(schema),
    mode: "all",
  });

  const { fields, remove, append } = useFieldArray({
    control,
    name: "listProductDetail",
  });

  const history = useHistory();
  const [disable, setDisable] = useState(false);

  // ** Function to change user image
  const onSelectFile = async (e) => {
    let files = e.target.files;
    const imageData = new FormData();
    imageData.append("file", files[0]);
    const result = await commonService.UploadFile(imageData);
    const name = e.target.getAttribute("name");

    const priority = e.target.getAttribute("priority");
    const productDetail = getValues().listProductDetail[priority];
    // [...productDetail.images,{image: result?.data?.url }]||[{image: result?.data?.url }]
    if (productDetail.images?.length > 0) {
      setValue(`listProductDetail.${priority}.images`, [
        ...productDetail.images,
        { image: result?.data?.url },
      ]);
    } else {
      setValue(`listProductDetail.${priority}.images`, [
        { image: result?.data?.url },
      ]);
    }

    setLoad(!load);
  };
  useEffect(() => {
    register(`listProductDetail.${0}.images`);
  }, []);

  const onSubmit = async (data) => {
    console.log(data);

    const customData = {
      // name: data?.name?.trim() || null,
      // gender: parseInt(data?.gender?.value),
      // description: data?.description,
      // brandId: data?.brandId?.value,
      // madeInId: data?.madeinId?.value,
      // listProductDetail:data?.listProductDetail
    };
    return;

    if (isObjEmpty(errors)) {
      setDisable(true);
      const result = await employeeService.CreateEmployee(customData);

      if (result.isSuccess) {
        toast.success("Thêm nhân viên thành công");
        history.push("/cms/employee/list");
      } else {
        setDisable(false);
      }
      setDisable(false);
    }
  };

  return (
    <Row>
      <Col sm="12">
        <Form onSubmit={handleSubmit(onSubmit)}>
          <Row className="mt-2">
            <Col sm="12">
              <Label for="nameVerticalIcons">Tên sản phẩm:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="name"
                  // className={classnames({ "is-invalid": errors["name"] })}
                  innerRef={register}
                />
              </InputGroup>
              {/* <p className="text-danger">{errors.name?.message}</p> */}
            </Col>
            <Col sm="12">
              <Label>Giới tính:</Label>
              <Controller
                control={control}
                name="gender"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Đang hoạt động", number: 1 },
                      { value: "0", label: "Ngừng hoạt động", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>
            <Col sm="12" className="mt-1">
              <Label>Thương hiệu:</Label>
              <Controller
                control={control}
                name="brandId"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Đang hoạt động", number: 1 },
                      { value: "0", label: "Ngừng hoạt động", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>

            <Col sm="12" className="mt-1">
              <Label>Loại sản phẩm:</Label>
              <Controller
                control={control}
                name="categoryId"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Đang hoạt động", number: 1 },
                      { value: "0", label: "Ngừng hoạt động", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>

            <Col sm="12" className="mt-1">
              <Label>Xuất xứ:</Label>
              <Controller
                control={control}
                name="madeinId"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Đang hoạt động", number: 1 },
                      { value: "0", label: "Ngừng hoạt động", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>
          </Row>

          <Button.Ripple className="my-3" color="primary" type="submit">
            Thông tin chi tiết
          </Button.Ripple>

          {fields.map((item, index) => {
            return (
              <CardAction
                title={"Chi tiết " + (index + 1)}
                actions="collapse"
                key={item.id}
                color="secondary"
                isOutline={true}
              >
                <CardBody>
                  <Row className="justify-content-start mb-2 px-1">
                    {getValues()?.listProductDetail &&
                      getValues()?.listProductDetail[index]?.images?.map(
                        (item, index) => {
                          return (
                            <div
                              key={index}
                              style={{
                                margin: "0px 10px",
                              }}
                            >
                              <img
                                className="user-avatar   cursor-pointer"
                                src={item?.image}
                                alt="user profile avatar"
                                height="87"
                                width="90"
                              />
                            </div>
                          );
                        }
                      )}
                    {
                      <Button.Ripple
                        className="btn-icon m-0 "
                        color="info"
                        id="image"
                        outline
                        tag={Label}
                      >
                        <input
                          type="file"
                          hidden
                          id="image"
                          // enctype="multipart/form-data"
                          onChange={onSelectFile}
                          accept="image/*"
                          // multiple
                          name={`listProductDetail.${index}.images`}
                          priority={index}
                        />
                        <Image size={20} />
                        <div
                          className="mt-1"
                          style={{ maxWidth: "65px", fontSize: "10px" }}
                        >
                          Thêm hình ảnh (
                          {getValues().listProductDetail &&
                            getValues().listProductDetail[index].images?.length}
                          /5)
                        </div>
                      </Button.Ripple>
                    }
                  </Row>
                  <Row>
                    <Col sm="5">
                      <Label>Màu sắc:</Label>
                      <Controller
                        control={control}
                        name={`listProductDetail.${index}.color`}
                        render={({ onChange, value }) => (
                          <Select
                            theme={selectThemeColors}
                            isClearable={false}
                            className="react-select"
                            classNamePrefix="select"
                            options={[
                              {
                                value: "1",
                                label: "Đang hoạt động",
                                number: 1,
                              },
                              {
                                value: "0",
                                label: "Ngừng hoạt động",
                                number: 2,
                              },
                            ]}
                            value={value}
                            innerRef={register}
                            onChange={onChange}
                          />
                        )}
                      />
                    </Col>
                    <Col sm="5">
                      <Label for="nameVerticalIcons">Giá:</Label>
                      <InputGroup className="input-group-merge" tag={FormGroup}>
                        <Input
                          name={`listProductDetail.${index}.price`}
                          // className={classnames({
                          //   "is-invalid":
                          //     errors[`listProductDetail.${index}.price`],
                          // })}
                          innerRef={register}
                        />
                      </InputGroup>
                      {/* <p className="text-danger">
                        {errors[`listProductDetail.${index}.price`]?.message}
                      </p> */}
                    </Col>
                  </Row>
                  <SizeAndAmount
                    nestIndex={index}
                    {...{ control, register, errors }}
                  />

                  <Row className="justify-content-end">
                    <Button.Ripple
                      outline
                      className="mt-1 mx-2"
                      color="danger"
                      onClick={() => {
                        // if (fields.length >= 2) {
                        // unregister(`listProductDetail.${index}.images`);
                        remove(index);
                        // }
                      }}
                    >
                      Xoá
                    </Button.Ripple>
                  </Row>
                </CardBody>
              </CardAction>
            );
          })}

          <Button.Ripple
            outline
            className="mt-2"
            color="info"
            onClick={() => {
              append({
                price: 1,
                color: { value: "0", label: "Nhân viên", number: 2 },
                images: [],
                sizeAndAmount: [
                  {
                    size: { value: "0", label: "Nhân viên", number: 2 },
                    amount: 0,
                  },
                ],
              });

              const index = getValues().listProductDetail.length;
              register(`listProductDetail.${index}.images`);
            }}
          >
            Thêm chi tiết
          </Button.Ripple>
          <Row>
            <Col sm="12" className="mt-2">
              <Label> Mô tả:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="description"
                  type="textarea"
                  className={classnames({
                    "is-invalid": errors["description"],
                  })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.description?.message}</p>
            </Col>
          </Row>

          <Row className="justify-content-end">
            <FormGroup className="d-flex mb-0 mx-1">
              <Button.Ripple
                className="mr-1"
                color="primary"
                type="submit"
                disabled={disable}
              >
                {disable ? (
                  <>
                    <Spinner size="sm" />
                    <span className="ml-50">Vui lòng đợi...</span>
                  </>
                ) : (
                  "Thêm"
                )}
              </Button.Ripple>
              <Button.Ripple
                outline
                color="secondary"
                onClick={() => {
                  history.push("/cms/employee/list");
                }}
              >
                Huỷ
              </Button.Ripple>
            </FormGroup>
          </Row>
        </Form>
      </Col>
    </Row>
  );
};
export default EmployeeTab;
