import Avatar from "@Components/avatar";
import employeeService from "@Services/employeeService";
import "@Styles/react/libs/flatpickr/flatpickr.scss";
import { selectThemeColors } from "@Utils";
import Flatpickr from "react-flatpickr";
import moment from "moment";
import { useState } from "react";
import Select from "react-select";
import {
  Button,
  Col,
  Form,
  FormGroup,
  Input,
  InputGroup,
  Label,
  Media,
  CustomInput,
  Row,
  Spinner,
} from "reactstrap";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

import { Edit, Trash2 } from "react-feather";
import { Controller, useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";
import classnames from "classnames";
import { SCHEMA_EMPLOYEE } from "@Constants/constValidate";

const statusObject = {
  1: { value: "1", label: "Đang hoạt động", number: 1 },
  0: { value: "0", label: "Ngừng hoạt động", number: 2 },
};
const roleObject = {
  1: { value: "1", label: "Quản trị viên", number: 1 },
  0: { value: "0", label: "Nhân viên", number: 2 },
};
const genderObject = {
  1: "Nam",
  0: "Nữ",
};
const schema = yup.object(SCHEMA_EMPLOYEE).required();

const EmployeeTab = ({ initial }) => {
  // ** States
  const [img, setImg] = useState(null);
  const [userData, setUserData] = useState(null);

  const [disable, setDisable] = useState(false);
  const initialValues = {
    ...initial,
    role: roleObject[initial.role],
    status: statusObject[initial.status],
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
    control,
    setValue,
  } = useForm({
    defaultValues: initialValues,

    resolver: yupResolver(schema),
    mode: "all",
  });
  const history = useHistory();
  // ** Function to change user image
  const onChange = (e) => {
    const reader = new FileReader(),
      files = e.target.files;
    reader.onload = function () {
      setImg(reader.result);
    };
    reader.readAsDataURL(files[0]);
  };

  const onSubmit = async (data) => {
    setDisable(true);
    const customData = {
      ...data,
      id: initial.id,
      image: "",
      role: data.role.value,
      status: data.status.value,
      birthDay:moment(data.birthday).format("DD/MM/yyyy"),
    };
    delete customData.createdAt
    const result = await employeeService.UpdateEmployee(customData);
    if (result.isSuccess) {
      toast.success("Cập nhật nhân viên thành công");
      history.push("/cms/employee/list");
    } else {
      setDisable(false);
    }
  };

  // ** Renders User
  const renderUserAvatar = () => {
    if (img === null) {
      const stateNum = Math.floor(Math.random() * 6),
        states = [
          "light-success",
          "light-danger",
          "light-warning",
          "light-info",
          "light-primary",
          "light-secondary",
        ],
        color = states[stateNum];
      return (
        <Avatar
          initials
          color={color}
          className="rounded mr-2 my-25"
          content={" "}
          contentStyles={{
            borderRadius: 0,
            fontSize: "calc(36px)",
            width: "100%",
            height: "100%",
          }}
          style={{
            height: "90px",
            width: "90px",
          }}
        />
      );
    } else {
      return (
        <img
          className="user-avatar rounded mr-2 my-25 cursor-pointer"
          src={img}
          alt="user profile avatar"
          height="90"
          width="90"
        />
      );
    }
  };

  return (
    <Row>
      <Col sm="4">
        <Media className="mb-2">
          {renderUserAvatar()}
          <Media className="mt-50" body>
            <h4>Ảnh </h4>
            <div className="d-flex flex-wrap mt-1 px-0">
              <Button.Ripple
                id="change-img"
                tag={Label}
                className="mr-75 mb-0"
                color="primary"
              >
                <span className="d-none d-sm-block">Tải lên</span>
                <span className="d-block d-sm-none">
                  <Edit size={14} />
                </span>
                <input
                  type="file"
                  hidden
                  id="change-img"
                  onChange={onChange}
                  accept="image/*"
                />
              </Button.Ripple>
              <Button.Ripple color="secondary" outline>
                <span className="d-none d-sm-block">Đặt lại</span>
                <span className="d-block d-sm-none">
                  <Trash2 size={14} />
                </span>
              </Button.Ripple>
            </div>
          </Media>
        </Media>
      </Col>

      <Col sm="12">
      <Form onSubmit={handleSubmit(onSubmit)}>
          <Row>
            <Col sm="4">
              <Label for="nameVerticalIcons">Họ và tên:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="name"
                  className={classnames({ "is-invalid": errors["name"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.name?.message}</p>
            </Col>
            <Col sm="4">
              <Label for="nameVerticalIcons">Tên đăng nhập:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="username"
                  className={classnames({ "is-invalid": errors["username"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.username?.message}</p>
            </Col>

            <Col sm="4">
              <FormGroup>
                <Label className="d-block" for="dob">
                  Ngày sinh
                </Label>
                <Controller
                  name="birthDay"
                  as={Flatpickr}
                  control={control}
                  placeholder="DD/MM/YYYY"
                  innerRef={register}
                  options={{
                    dateFormat: "d-m-Y",
                    maxDate: moment(new Date()).format("DD/MM/yyyy"),
                  }}
                  className={classnames("form-control", {
                    "is-invalid": errors["birthDay"],
                  })}
                />
                <p className="text-danger">{errors.birthDay?.message}</p>
              </FormGroup>
            </Col>
            <Col sm="4">
              <Label>Số điện thoại:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="phone"
                  className={classnames({ "is-invalid": errors["phone"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.phone?.message}</p>
            </Col>

            <Col sm="4">
              <Label className="d-block mb-1">Giới tính:</Label>

              <FormGroup>
                <Controller
                  name="gender"
                  control={control}
                  innerRef={register}
                  render={(props) => {
                    return (
                      <CustomInput
                        inline
                        type="radio"
                        label="Nam"
                        value="1"
                        defaultChecked={initial.gender=="1"}
                        onChange={() => setValue("gender", "1")}
                      />
                    );
                  }}
                />
                <Controller
                  name="gender"
                  control={control}
                  innerRef={register}
                  render={(props) => {
                    return (
                      <CustomInput
                        inline
                        type="radio"
                        label="Nữ"
                        value="0"
                        defaultChecked={initial.gender=="0"}
                        onChange={() => setValue("gender", "0")}
                      />
                    );
                  }}
                />
              </FormGroup>
            </Col>
            <Col sm="4">
              <Label> Email:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="email"
                  className={classnames({ "is-invalid": errors["email"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.email?.message}</p>
            </Col>
            <Col sm="4">
              <Label> CMND/CCCD:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="identityCard"
                  className={classnames({
                    "is-invalid": errors["identityCard"],
                  })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.identityCard?.message}</p>
            </Col>
            <Col sm="4">
              <Label> Vai trò:</Label>
              <Controller
                control={control}
                name="role"

                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Quản trị viên", number: 1 },
                      { value: "0", label: "Nhân viên", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>
            <Col sm="4">
              <Label>Trạng thái:</Label>
              <Controller
                control={control}
                name="status"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Đang hoạt động", number: 1 },
                      { value: "0", label: "Ngừng hoạt động", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>
            <Col sm="12">
              <Label> Ghi chú:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="note"
                  type="textarea"
                  className={classnames({ "is-invalid": errors["note"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.note?.message}</p>
            </Col>
            <Col sm="4">
              <Label for="nameVerticalIcons">Ngày tạo:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input name="createdAt" innerRef={register} disabled />
              </InputGroup>
            </Col>
            <Col sm="4">
              <Label for="nameVerticalIcons">Ngày cập nhật:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input name="updatedAt" innerRef={register} disabled />
              </InputGroup>
            </Col>
            <Col sm="4">
              <Label for="nameVerticalIcons">Cập nhật bởi:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input name="updatedBy" innerRef={register} disabled />
              </InputGroup>
            </Col>
          </Row>
          <Row className="justify-content-end">
            {/* <Col sm="4"> */}
            <FormGroup className="d-flex mb-0 mx-1">
              <Button.Ripple className="mr-1" color="primary" type="submit"
                disabled={disable}
                >

                {disable ? (
                  <>
                    <Spinner size="sm" />
                    <span className="ml-50">Vui lòng đợi...</span>
                  </>
                ) : (
                  "Cập nhật"
                )}
              </Button.Ripple>
              <Button.Ripple outline color="secondary" onClick={()=>{
                history.push("/cms/employee/list")
              }}>
                Huỷ
              </Button.Ripple>
            </FormGroup>
            {/* </Col> */}
          </Row>
        </Form>

      </Col>
    </Row>
  );
};
export default EmployeeTab;
