import ExtensionsHeader from "@Components/extensions-header";
import ProductService from "@Services/productService";
import "@Styles/react/libs/react-select/_react-select.scss";
import "@Styles/react/libs/tables/react-dataTable-component.scss";
import { selectThemeColors } from "@Utils";
import { Fragment, useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import { ChevronDown } from "react-feather";
import { Controller, useForm } from "react-hook-form";
import ReactPaginate from "react-paginate";
import Select from "react-select";
import {
  Button,
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Col,
  CustomInput,
  Form,
  Input,
  Label,
  Row
} from "reactstrap";

import columns from "./Columns";
const CustomHeader =(handlePerPage)=> {
  return (
    <div className="invoice-list-table-header w-100 mr-1 ml-50 mt-2 mb-75">
      <Row>
        <Col xl="6" className="d-flex align-items-center p-0">
          <div className="d-flex align-items-center w-100">
            <Label for="rows-per-page">Hiển thị</Label>
            <CustomInput
              className="form-control mx-50"
              type="select"
              id="rows-per-page"
              onChange={(e) => {
                handlePerPage({
                  pageSize: e.target.value,
                });
              }}
              style={{
                width: "5rem",
                padding: "0 0.8rem",
                backgroundPosition:
                  "calc(100% - 3px) 11px, calc(100% - 20px) 13px, 100% 0",
              }}
            >
              <option value="10">10</option>
              <option value="20">20</option>
              <option value="50">50</option>
              <option value="100">100</option>
            </CustomInput>
            <Label for="rows-per-page">dòng</Label>
          </div>
        </Col>
        <Col
          xl="6"
          className="d-flex align-items-sm-center justify-content-lg-end justify-content-start flex-lg-nowrap flex-wrap flex-sm-row flex-column pr-lg-1 p-0 mt-lg-0 mt-1"
        >
          {/* <Button.Ripple
            color="primary"
            className="mx-1"
            tag={Link}
            to="/cms/Customer/add"
          >
            Thêm mới
          </Button.Ripple> */}
        </Col>
      </Row>
    </div>
  );
};

const EmployeesList = (props)=> {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm();
  const [employeeData, setEmployeeData] = useState({
    data: [],
    currentPage: 0,
    countItems: null,
    countPages: null,
  });

  async function handleGetListEmployee(value) {
    const data = {
      name: value?.name?.trim() || null,

      status: value?.status?.value || null,
      gender: value?.gender?.value || null,
      available: value?.available?.value || null,
      brand: {
        id: value?.brand_id ||null,
      },
      category: {
        id: value?.category_id ||null,
      },
      pageNumber:
        value?.pageNumber >= 0 ? value.pageNumber : employeeData.currentPage,
      pageSize: value?.pageSize || 10,
      sortBy: value?.sortBy || "id",
      sortDirection: value.sortDirection || "ASC",
    };
    console.log(employeeData.countPages);

    const result = await ProductService.getListEmployee(data);
    result.isSuccess && setEmployeeData(result?.data);
  }
  useEffect(() => {
    handleGetListEmployee({});
  }, []);

  const onSubmit = (data) => {

    handleGetListEmployee({ ...data, pageNumber: 0 });
  };
  const CustomPagination = () => {
    return (
      <ReactPaginate
        previousLabel={""}
        nextLabel={""}
        pageCount={employeeData.countPages || 1}
        activeClassName="active"
        forcePage={employeeData.currentPage}
        onPageChange={(page) => {
          handleGetListEmployee({
            pageNumber: page.selected,
          });
        }}
        pageClassName={"page-item"}
        nextLinkClassName={"page-link"}
        nextClassName={"page-item next"}
        previousClassName={"page-item prev"}
        previousLinkClassName={"page-link"}
        pageLinkClassName={"page-link"}
        containerClassName={
          "pagination react-paginate justify-content-end my-2 pr-1"
        }
      />
    );
  };

  const handleSort = async (column, sortDirection) => {
    handleGetListEmployee({
      sortBy: column.sortField,
      sortDirection: sortDirection.toUpperCase(),
    });
  };
  return (
    <Fragment>
      <ExtensionsHeader title="Sản phẩm" />

      <Card>
        <CardHeader>
          <CardTitle tag="h4">Lọc dữ liệu</CardTitle>
        </CardHeader>
        <CardBody className="px-3 py-1">
          <Form onSubmit={handleSubmit(onSubmit)}>
            <Row>
              <Col md="3" className="mb-2">
                <Label for="search-invoice">Tên sản phẩm:</Label>
                <Input name="name" 

                {...register("name",)}/>
              </Col>
              {/* <Col md="3" className="mb-2">
                <Label for="search-invoice">Giới tính:</Label>
                <Controller
                  control={control}
                  name="gender"
                  render={({ onChange, value }) => (
                    <Select
                      theme={selectThemeColors}
                      isClearable={false}
                      className="react-select"
                      placeholder="Chọn giới tính"
                      classNamePrefix="select"
                      options={[
                        { value: null, label: "Chọn giới tính", number: 0 },
                        { value: "1", label: "Nam", number: 2 },
                        { value: "0", label: "Nữ", number: 3 },
                      ]}
                      value={value}
                      onChange={onChange}
                    />
                  )}
                />
              </Col>
              <Col md="3" className="mb-2">
                <Label for="search-invoice">Loại sản phẩm:</Label>
                <Controller
                  control={control}
                  name="status"
                  render={({ onChange, value }) => (
                    <Select
                      theme={selectThemeColors}
                      isClearable={false}
                      className="react-select"
                      placeholder="Chọn loại sản phẩm"
                      classNamePrefix="select"
                      options={[
                        { value: null, label: "Chọn loại sản phẩm", number: 0 },
                        { value: "1", label: "Đang hoạt động", number: 2 },
                        { value: "0", label: "Ngừng hoạt động", number: 3 },
                      ]}
                      value={value}
                      onChange={onChange}
                    />
                  )}
                />
              </Col>
              <Col md="3" className="mb-2">
                <Label for="search-invoice">Thương hiệu:</Label>
                <Controller
                  control={control}
                  name="status"
                  render={({ onChange, value }) => (
                    <Select
                      theme={selectThemeColors}
                      isClearable={false}
                      className="react-select"
                      placeholder="Chọn loại sản phẩm"
                      classNamePrefix="select"
                      options={[
                        { value: null, label: "Chọn loại sản phẩm", number: 0 },
                        { value: "1", label: "Đang hoạt động", number: 2 },
                        { value: "0", label: "Ngừng hoạt động", number: 3 },
                      ]}
                      value={value}
                      onChange={onChange}
                    />
                  )}
                />
              </Col>
              <Col md="3" className="mb-2">
                <Label for="search-invoice">Trạng thái:</Label>
                <Controller
                  control={control}
                  name="status"
                  render={({ onChange, value }) => (
                    <Select
                      theme={selectThemeColors}
                      isClearable={false}
                      className="react-select"
                      placeholder="Chọn trạng thái"
                      classNamePrefix="select"
                      options={[
                        { value: null, label: "Chọn trạng thái", number: 0 },
                        { value: "1", label: "Đang hoạt động", number: 2 },
                        { value: "0", label: "Ngừng hoạt động", number: 3 },
                      ]}
                      value={value}
                      onChange={onChange}
                    />
                  )}
                />
              </Col>

              <Col md="3" className="mb-2">
                <Label for="search-invoice">Kích hoạt:</Label>
                <Controller
                  control={control}
                  name="available"
                  render={({ onChange, value }) => (
                    <Select
                      theme={selectThemeColors}
                      isClearable={false}
                      className="react-select"
                      placeholder="Chọn trạng thái"
                      classNamePrefix="select"
                      options={[
                        { value: null, label: "Chọn trạng thái", number: 0 },
                        { value: "1", label: "Đã kích hoạt", number: 2 },
                        { value: "0", label: "Chưa kích hoạt", number: 3 },
                      ]}
                      value={value}
                      onChange={onChange}
                    />
                  )}
                />
              </Col> */}
            </Row>
            <Row className="justify-content-end mt-2">
              <Button.Ripple color="primary" className="mx-1" type="submit">
                Lọc dữ liệu
              </Button.Ripple>
            </Row>
          </Form>
        </CardBody>
      </Card>
      <Card>
        <DataTable
          noHeader
          title="Desserts"
          pagination
          highlightOnHover
          subHeader
          responsive
          paginationServer
          sortServer
          onSelectedRowsChange={props.onSelectedRow}
        selectableRows={props.isModal||false}
          columns={columns}
          onSort={handleSort}
          sortIcon={<ChevronDown />}
          className="react-dataTable"
          paginationComponent={CustomPagination}
          data={employeeData?.data}
          subHeaderComponent={
            <CustomHeader handlePerPage={handleGetListEmployee} props={props}/>
          }
        />
      </Card>
    </Fragment>
  );
};

export default EmployeesList;
