// ** React Imports
import { useEffect, useState } from "react";

import brandService from "@Services/brandService";
import productSetService from "@Services/productSetService";
import "@Styles/react/libs/flatpickr/flatpickr.scss";
import { selectThemeColors } from "@Utils";
import Select from "react-select";
// ** Third Party Components
import { SCHEMA_CREATE_BRAND } from "@Constants/constValidate";
import { yupResolver } from "@hookform/resolvers/yup";
import { isObjEmpty } from "@Utils";
import {
  Button,
  Col, Form,
  FormGroup,
  Input,
  InputGroup,
  Label, Row,
  Spinner
} from "reactstrap";

import classnames from "classnames";
import * as yup from "yup";

import { Controller, useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";

const schema = yup.object(SCHEMA_CREATE_BRAND).required();
const statusObject = {
  1: { value: "1", label: "Đang kinh doanh", number: 1 },
  0: { value: "0", label: "Ngừng kinh doanh", number: 2 },
};
const EmployeeTab = ({ initial }) => {

  // ** States
  const [optionProductSet, setOptionProductSet] = useState();
  const initialValues = {
    ...initial,
    status: statusObject[initial.status],
  };
  const {
    register,
    handleSubmit,
    formState: { errors },
    control,
    setValue,
  } = useForm({
    defaultValues: initialValues,

    resolver: yupResolver(schema),
    mode: "all",
  });
  const history = useHistory();
  const [disable, setDisable] = useState(false);

  const onSubmit = async (data) => {
    console.log(data);
    const customData = {
      id:initial.id,
      name: data.name,
      productSetId: data?.productSet?.value,
      status: data?.status?.value,
    };

    if (isObjEmpty(errors)) {
      setDisable(true);
      const result = await brandService.UpdateEmployee(customData);
      if (result.isSuccess) {
        toast.success("Cập nhật thương hiệu thành công");
        history.push("/cms/classify/brand/list");
      } else {
        setDisable(false);
      }
      setDisable(false);
    }
  };

  // useEffect(() => {
  //   handleGetListProductSet();
  // }, []);
  // async function handleGetListProductSet() {
  //   const result = await productSetService.getAllProductSet();
  //   if (result.isSuccess) {
  //     const _optionProductSet = [];
  //     result.data.data?.map((item, index) => {
  //       _optionProductSet.push({
  //         value: item.id,
  //         label: item.name,
  //         number: index,
  //       });
  //     });
  //     setOptionProductSet(_optionProductSet);
  //     setValue("productSet", {
  //       value: initial.productSet.id,
  //       label: initial.productSet.name,
  //       number: null
  //     });
  //   }
  // }
  return (
    <Row>
      <Col sm="12">
        <Form onSubmit={handleSubmit(onSubmit)}>
          <Row>
            <Col sm="4">
              <Label for="nameVerticalIcons">Tên thương hiệu:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="name"
                  className={classnames({ "is-invalid": errors["name"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.name?.message}</p>
            </Col>

            {/* <Col sm="4">
              <Label for="search-invoice">Tập sản phẩm:</Label>
              <Controller
                control={control}
                name="productSet"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    placeholder="Chọn tập sản phẩm"
                    classNamePrefix="select"
                    options={optionProductSet}
                    value={value}
                    className={classnames("react-select", {
                      "is-invalid": errors["productSet"],
                    })}
                    onChange={onChange}
                  />
                )}
              />
              <p className="text-danger">{errors.productSet?.message}</p>
            </Col> */}
            <Col sm="4">
              <Label>Trạng thái:</Label>
              <Controller
                control={control}
                name="status"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Đang kinh doanh", number: 1 },
                      { value: "0", label: "Ngừng kinh doanh", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>
            <Col sm="4">
              <Label for="nameVerticalIcons">Ngày tạo:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input name="createdAt" innerRef={register} disabled />
              </InputGroup>
            </Col>
            <Col sm="4">
              <Label for="nameVerticalIcons">Ngày cập nhật:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input name="updatedAt" innerRef={register} disabled />
              </InputGroup>
            </Col>
            <Col sm="4">
              <Label for="nameVerticalIcons">Cập nhật bởi:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input name="updatedBy" innerRef={register} disabled />
              </InputGroup>
            </Col>
          </Row>
          <Row className="justify-content-end">
            <FormGroup className="d-flex mb-0 mx-1">
              <Button.Ripple
                className="mr-1"
                color="primary"
                type="submit"
                disabled={disable}
              >
                {disable ? (
                  <>
                    <Spinner size="sm" />
                    <span className="ml-50">Vui lòng đợi...</span>
                  </>
                ) : (
                  "Cập nhật"
                )}
              </Button.Ripple>
              <Button.Ripple
                outline
                color="secondary"
                onClick={() => {
                  history.push("/cms/classify/brand/list");
                }}
              >
                Huỷ
              </Button.Ripple>
            </FormGroup>
            {/* </Col> */}
          </Row>
        </Form>
      </Col>
    </Row>
  );
};
export default EmployeeTab;
