// **  Initial State
const initialState = {
  userData: {}
}

const authReducer = (state = initialState, action:any) => {
  switch (action.type) {
    case 'LOGIN':
      return {
        ...state,
        userData: action.data,
        accessToken: action.data.accessToken,
        refreshToken: action.data.refreshToken
      }
    case 'LOGOUT':
      const obj = { ...action }
      delete obj.type
      return { ...state, userData: {}, ...obj }
    default:
      return state
  }
}

export default authReducer
