import { Mail, MessageSquare,ShoppingBag, CheckSquare, Calendar, AlignLeft, Circle, ShoppingCart, User, List, Server, Grid } from 'react-feather'

export default [
  {
    header: 'Sản phẩm'
  },
  {
    id: 'classify-product',
    title: 'Danh mục sản phẩm',
    icon: <List size={20} />,
    children: [
      {
        id: "product-set",
        title: "Tập sản phẩm",
        icon: <AlignLeft size={12} />,
        navLink: "/cms/classify/product-set/list",
         children: [
      {
        id: "list",
        title: "Danh sách",
        icon: <Circle size={12} />,
        navLink: "/cms/classify/product-set/list",
      },
      {
        id: "add",
        title: "Thêm",
        icon: <Circle size={12} />,
        navLink: "/cms/classify/product-set/add",
      },
    ],
      },
      {
        id: "brand",
        title: "Thương hiệu",
        icon: <AlignLeft size={12} />,
        navLink: "/cms/classify/brand/list",
         children: [
      {
        id: "list",
        title: "Danh sách",
        icon: <Circle size={12} />,
        navLink: "/cms/classify/brand/list",
      },
      {
        id: "add",
        title: "Thêm",
        icon: <Circle size={12} />,
        navLink: "/cms/classify/brand/add",
      },
    ],
      },

      {
        id: "category",
        title: "Loại sản phẩm",
        icon: <AlignLeft size={12} />,
        navLink: "/cms/classify/category/list",
         children: [
      {
        id: "list",
        title: "Danh sách",
        icon: <Circle size={12} />,
        navLink: "/cms/classify/category/list",
      },
      {
        id: "add",
        title: "Thêm",
        icon: <Circle size={12} />,
        navLink: "/cms/classify/category/add",
      },
    ],
      },

    ],

  },
  {
    id: 'property-product',
    title: 'Thuộc tính sản phẩm',
    icon: <Grid size={20} />,
    navLink: '/cms/property/made-in/list',
    children: [
      {
        id: "madein",
        title: "Xuất xứ",
        icon: <AlignLeft size={12} />,
        children:[
          {
            id: "list",
            title: "Danh sách",
            icon: <Circle size={12} />,
            navLink: "/cms/property/made-in/list",
          },
          {
            id: "add",
            title: "Thêm",
            icon: <Circle size={12} />,
            navLink: "/cms/property/made-in/add",
          },
        ]
      },


      {
        id: "size",
        title: "Kích thước",
        icon: <AlignLeft size={12} />,
        children:[
          {
            id: "list",
            title: "Danh sách",
            icon: <Circle size={12} />,
            navLink: "/cms/property/size/list",
          },
          {
            id: "add",
            title: "Thêm",
            icon: <Circle size={12} />,
            navLink: "/cms/property/size/add",
          },
        ]
      },



      {
        id: "color",
        title: "Màu sắc",
        icon: <AlignLeft size={12} />,
        children:[
          {
            id: "list",
            title: "Danh sách",
            icon: <Circle size={12} />,
            navLink: "/cms/property/color/list",
          },
          {
            id: "add",
            title: "Thêm",
            icon: <Circle size={12} />,
            navLink: "/cms/property/color/add",
          },
        ]
      },
    ],
  },
  {
    id: 'product',
    title: 'Sản phẩm',
    icon: <ShoppingBag size={20} />,
    navLink: '/cms/product/list',
    children: [
      {
        id: "list",
        title: "Danh sách",
        icon: <Circle size={12} />,
        navLink: "/cms/product/list",
      },
      {
        id: "add",
        title: "Thêm",
        icon: <Circle size={12} />,
        navLink: "/cms/product/add",
      },
    ],
  },
  {
    id: 'combo-product',
    title: 'Combo sản phẩm',
    icon: <Server size={20} />,
    navLink: '/cms/customer/list',
    children: [
      {
        id: "list",
        title: "Danh sách",
        icon: <Circle size={12} />,
        navLink: "/cms/property/made-in/list",
      },
      {
        id: "add",
        title: "Thêm",
        icon: <Circle size={12} />,
        navLink: "/cms/property/made-in/add",
      },
    ],
  },
]
