// ** React Imports
import { Copy, Download, Edit, Eye, Trash } from "react-feather";

// ** Invoice List Sidebar
// ** Store & Actions
// import UserService from "@"
// ** Third Party Components
import { UncontrolledTooltip } from "reactstrap";

// ** React Imports
import { Link } from "react-router-dom";


// ** Custom Components
import Avatar from "@Components/avatar";

// ** Store & Actions
// import { getUser, deleteUser } from "../store/action";
// import { store } from "@Store/storeConfig/store";

// ** Third Pasearsẻarty Components
import {
    Database,
    Edit2,
    MoreVertical, Settings, Slack,
    User
} from "react-feather";
import {
    Badge, DropdownItem, DropdownMenu, DropdownToggle, UncontrolledDropdown
} from "reactstrap";
// ** Styles
import "@Styles/react/libs/react-select/_react-select.scss";
import "@Styles/react/libs/tables/react-dataTable-component.scss";
// ** Renders Role Columns
const renderRole = (row: any) => {
  const roleObj: any = {
    subscriber: {
      class: "text-primary",
      icon: User,
    },
    maintainer: {
      class: "text-success",
      icon: Database,
    },
    editor: {
      class: "text-info",
      icon: Edit2,
    },
    author: {
      class: "text-warning",
      icon: Settings,
    },
    admin: {
      class: "text-danger",
      icon: Slack,
    },
  };

  const Icon = roleObj[row.role] ? roleObj[row.role].icon : Edit2;

  return (
    <span className="text-truncate text-capitalize align-middle">
      <Icon
        size={18}
        className={`${roleObj[row.role] ? roleObj[row.role].class : ""} mr-50`}
      />
      {row.role}
    </span>
  );
};

const statusMail: any = {
  0: {
    text: "unconfirmed",
    bgr: "light-warning",
  },
  1: {
    text: "confirmed",
    bgr: "light-success",
  },
};

const statusBlock: any = {
  1: {
    text: "Đang hoạt động",
    bgr: "light-success",
  },
  0: {
    text: "Không hoạt động",
    bgr: "light-danger",
  },
};
const columns = [
    {
      name: "id",
      minWidth: "60px",
      selector: "id",
      cell: (row: any) => row.id,
    },
    {
      name: "Ảnh",
      minWidth: "70px",
      selector: "id",
      cell: (row: any) =>
      {
        return (
       row.image? <Avatar className="mr-1" img={row.image} width="32" height="32" />: <Avatar
        color={ "primary"}
        className="mr-1"
        content={row.name||" "}
        initials
      />);
      }

    },
    {
      name: "Tên đăng nhập",
      minWidth: "200px",
      selector: "memb___id",
      // sortable: true,
      cell: (row: any) => row.username,
    },
    {
      name: "Họ và tên",
      minWidth: "170px",
      selector: "email",
      // sortable: true,
      cell: (row: any) => row.name,
    },
    {
      name: "Email",
      minWidth: "250px",
      selector: "id",
      // sortable: true,
      cell: (row: any) => row.email,
    },
    {
      name: "Số điện thoại",
      minWidth: "180px",
      selector: "id",
      // sortable: true,
      cell: (row: any) => row.phone,
    },
    {
      name: "CMND",
      minWidth: "170px",
      selector: "id",
      // sortable: true,
      cell: (row: any) => row.identityCard,
    },
    {
      name: "Trạng thái",
      minWidth: "158px",
      selector: "status",
      sortable: true,
      cell: (row: any) => (
        <Badge
          className="text-capitalize"
          color={statusBlock[row.status].bgr}
          pill
        >
          {statusBlock[row.status].text}
        </Badge>
      ),
    },


    {
      name: "Thao tác",
      minWidth: "150px",
      selector: "",
      sortable: true,
      cell: (row: any) => (
        <div className="column-action d-flex align-items-center">
          {/* <Send size={17} id={`send-tooltip-${row.id}`} /> */}
          {/* <UncontrolledTooltip placement="top" target={`send-tooltip-${row.id}`}>
            Send Mail
          </UncontrolledTooltip> */}
          {/* <Link
            to={`/apps/invoice/preview/${row.id}`}
            id={`pw-tooltip-${row.id}`}
          >
            <Eye size={17} className="mx-1" />
          </Link>
          <UncontrolledTooltip placement="top" target={`pw-tooltip-${row.id}`}>
            Preview Invoice
          </UncontrolledTooltip> */}
          <UncontrolledDropdown>
            <DropdownToggle tag="span">
              <MoreVertical size={17} className="cursor-pointer" />
            </DropdownToggle>
            <DropdownMenu right>
              {/* <DropdownItem
                tag="a"
                href="/"
                className="w-100"
                // onClick={e => e.preventDefault()}
              >
                <Download size={14} className="mr-50" />
                <span className="align-middle">Download</span>
              </DropdownItem> */}
              <DropdownItem
                tag={Link}
                to={`/cms/employee/edit/${row.id}`}
                className="w-100"
              >
                <Edit size={14} className="mr-50" />
                <span className="align-middle">Xem/Sửa</span>
              </DropdownItem>
              {/* <DropdownItem
                tag="a"
                href="/"
                className="w-100"
                // onClick={e => {
                //   e.preventDefault()
                //   store.dispatch(deleteInvoice(row.id))
                // }}
              >
                <Trash size={14} className="mr-50" />
                <span className="align-middle">Delete</span>
              </DropdownItem>
              <DropdownItem
                tag="a"
                href="/"
                className="w-100"
                onClick={(e) => e.preventDefault()}
              >
                <Copy size={14} className="mr-50" />
                <span className="align-middle">Duplicate</span>
              </DropdownItem> */}
            </DropdownMenu>
          </UncontrolledDropdown>
        </div>
      ),
    },
  ];

  export default columns
