// ** React Imports
import { useEffect, useState } from 'react'

// ** User Edit Components
import EmployyeTab from './Employee'


// ** Store & Actions

// ** Third Party Components
import { Info, Share2, User } from 'react-feather'
import { Card, CardBody, Col, Nav, NavItem, NavLink, Row, TabContent, TabPane ,Alert} from 'reactstrap'

// ** Styles
import '@Styles/react/apps/app-users.scss'
import { useParams } from 'react-router-dom'
import EmployeeService from '@Services/employeeService'

const EmployeeEdit = () => {
  // ** States & Vars
  const [activeTab, setActiveTab] = useState('1');
const [data,setData] = useState();
  // ** Function to toggle tabs
  const toggle = (tab) => setActiveTab(tab)
  const {id} = useParams()
useEffect(()=>{
const init= async()=>{
  const result= await  EmployeeService.GetDetailEmployee(id);
  if (result.data) {
    setData(result.data)
  }
}
init();
},[])

  return data?  (
    <Row className='app-user-edit'>
      <Col sm='12'>
        <Card>
          <CardBody className='pt-2'>
            <Nav pills>
              <NavItem>
                <NavLink active={activeTab === '1'} onClick={() => toggle('1')}>
                  <User size={14} />
                  <span className='align-middle d-none d-sm-block'>Thông tin nhân viên</span>
                </NavLink>
              </NavItem>
            </Nav>
            <TabContent activeTab={activeTab}>
              <TabPane tabId='1'>
                <EmployyeTab  initial={data}/>
              </TabPane>
            </TabContent>
          </CardBody>
        </Card>
      </Col>
    </Row>
  ): 
  <Alert color="danger">
  <h4 className="alert-heading">Nhân viên Không tồn tại</h4>
</Alert>
}
export default EmployeeEdit
