import Avatar from "@Components/avatar";
import "@Styles/react/libs/react-select/_react-select.scss";
import "@Styles/react/libs/tables/react-dataTable-component.scss";
import { Edit, MoreVertical } from "react-feather";
import { Link } from "react-router-dom";
import {
  Badge,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  UncontrolledDropdown,
} from "reactstrap";
const statusBlock = {
  1: {
    text: "Đang hoạt động",
    bgr: "light-success",
  },
  0: {
    text: "Không hoạt động",
    bgr: "light-danger",
  },
};
const availableBlock = {
  1: {
    text: "Đã kích hoạt",
    bgr: "light-success",
  },
  0: {
    text: "Chưa kích hoạt",
    bgr: "light-danger",
  },

};
const genderBlock = {
  1: {
    text: "Nam",
  },
  0: {
    text: "Nữ",
  },
};
const columns = [
  {
    name: "id",
    minWidth: "60px",
    selector: (row) => row.id,
    sortable: true,

    sortField: "id",
    cell: (row) => row.id,
  },
  {
    name: "Tên sản phẩm",
    minWidth: "380px",
    selector: (row) => row.name,
    sortable: true,

    sortField: "name",
    cell: (row) => row.name,
  },
  {
    name: "Giới tính",
    minWidth: "140px",
    selector: (row) => row.gender,
    sortable: true,

    sortField: "gender",
    cell: (row) => (
      genderBlock[row.gender].text
    ),
  },
  {
    name: "Loại sản phẩm",
    minWidth: "200px",
    selector: (row) => row?.category?.name,
    // sortable: true,

    sortField: "name",
    cell: (row) => row?.category?.name,
  },
  {
    name: "Thương hiệu",
    minWidth: "170px",
    selector: (row) => row?.brand?.name,
    // sortable: true,

    sortField: "name",
    cell: (row) => row?.brand?.name,
  },
  {
    name: "Xuất xứ",
    minWidth: "170px",
    selector: (row) => row?.madeIn?.name,
    // sortable: true,

    sortField: "name",
    cell: (row) => row?.madeIn?.name,
  },
  {
    name: "Có sẵn",
    minWidth: "130px",
    selector: (row) => row.totalAmount,
    sortable: true,

    sortField: "totalAmount",
    cell: (row) => row.totalAmount,
  },

  {
    name: "Trạng thái",
    minWidth: "158px",
    selector: (row) => row.status,
    sortable: true,

    sortField: "status",
    cell: (row) => (
      <Badge
        className="text-capitalize"
        color={statusBlock[row.status].bgr}
        pill
      >
        {statusBlock[row.status].text}
      </Badge>
    ),
  },
  {
    name: "Kích hoạt",
    minWidth: "158px",
    selector: (row) => row.available,
    sortable: true,

    sortField: "available",
    cell: (row) => (
      // <Badge
      //   className="text-capitalize"
      //   color={availableBlock[row.available].bgr}
      //   pill
      // >
      //   {availableBlock[row.available].text}
      // </Badge>
      <div className='demo-inline-spacing'>
      <CustomInput
        type='switch'
        id='exampleCustomSwitch'
        name='customSwitch'
        label='Toggle this switch element'
        inline
      />
      <CustomInput
        type='switch'
        id='exampleCustomSwitchdisabled'
        name='customSwitchdisabled'
        label='Disabled switch element'
        inline
        disabled
      />
    </div>
    ),
  },
  {
    name: "Thao tác",
    minWidth: "150px",
    selector: "",
    sortable: true,

    cell: (row) => (
      <div className="column-action d-flex align-items-center">
        <UncontrolledDropdown>
          <DropdownToggle tag="span">
            <MoreVertical size={17} className="cursor-pointer" />
          </DropdownToggle>
          <DropdownMenu right>
            <DropdownItem
              tag={Link}
              to={`/cms/product/edit/${row.id}`}
              className="w-100"
            >
              <Edit size={14} className="mr-50" />
              <span className="align-middle">Xem/Sửa</span>
            </DropdownItem>
          </DropdownMenu>
        </UncontrolledDropdown>
      </div>
    ),
  },
];

export default columns;
