import * as yup from "yup";
import { REGEX_EMAIL, REGEX_PASSWORD, REGEX_PHONE } from "./regex";
export const SCHEMA_LOGIN = {
  username: yup.string().trim().required("Vui lòng nhập tên đăng nhập").min(3, "Tên đăng nhập phải tối thiểu 3 ký tự").max(30, "Tên đăng nhập tối đa 30 ký tự"),
  password: yup.string().trim().required("Vui lòng nhập mật khẩu").matches(REGEX_PASSWORD, "Mật khẩu phải tối thiểu 8 ký tự ít nhất một ký tự hoa, một ký tự thường, một ký tự số và một ký tự đặc biệt")
};
export const SCHEMA_FORGOT_PASSWORD = {
  email: yup.string().trim().required("Vui lòng nhập email").matches(REGEX_EMAIL, "Email không hợp lệ")
};
export const SCHEMA_CHANGE_PASSWORD = {
  current_password: yup.string().trim().required("Vui lòng nhập mật khẩu cũ"),
  new_password: yup.string().trim().required("Vui lòng nhập mật mới").matches(REGEX_PASSWORD, "Mật khẩu phải tối thiểu 8 ký tự ít nhất một ký tự hoa, một ký tự thường, một ký tự số và một ký tự đặc biệt"),
  retype_new_password: yup.string().required("Vui lòng nhập lại mật khẩu mới").oneOf([yup.ref(`new_password`), null], "Mật khẩu mới không khớp")
};
export const SCHEMA_CREATE_EMPLOYEE = {
  username: yup.string().trim().required("Vui lòng nhập tên đăng nhập").min(3, "Tên đăng nhập phải tối thiểu 3 ký tự").max(30, "Tên đăng nhập tối đa 30 ký tự"),
  name: yup.string().trim().required("Vui lòng nhập họ tên"),
  identityCard: yup.string().trim().required("Vui lòng nhập CMND/CCCD"),
  phone: yup.string().trim().required("Vui lòng nhập số điện thoại").matches(REGEX_PHONE, "Số điện thoại không hợp lệ"),
  email: yup.string().trim().required("Vui lòng nhập email").matches(REGEX_EMAIL, "Email không hợp lệ"),
  birthday: yup.date().required("Vui lòng chọn ngày sinh")
};
