import CardAction from "@Components/card-actions";
import { SCHEMA_PRODUCT } from "@Constants/constValidate";
import brandService from "@Services/brandService";
import categoryService from "@Services/categoryService";
import colorService from "@Services/colorService";
import commonService from "@Services/commonService";
import madeinService from "@Services/madeInService";
import productService from "@Services/productService";
import sizeService from "@Services/sizeService";
import "@Styles/react/libs/flatpickr/flatpickr.scss";
import { isObjEmpty, selectThemeColors } from "@Utils";
import { useEffect, useState } from "react";
import Select from "react-select";
import {
  Button,
  CardBody,
  Col,
  Form,
  FormGroup,
  Input,
  InputGroup,
  Label,
  Row,
  Spinner
} from "reactstrap";

import classnames from "classnames";
import * as yup from "yup";

import { Controller, useFieldArray, useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";
import ProductImage from "./ProductImage";
import SizeAndAmount from "./SizeAndAmount";

const initialValues = {
  gender: null,
  role: { value: "0", label: "Nhân viên", number: 2 },
  status: {
    value: "1",
    label: "Đang hoạt động",
    number: 1,
  },
  listProductDetail: [
    {
      price: 0,
      color: null,
      sizeAndAmount: [{ size: null, amount: 0 }],
    },
  ],
};
const schema = yup.object(SCHEMA_PRODUCT).required();
const EmployeeTab = ({}) => {
  const [load, setLoad] = useState(true);
  const [listImage, setListImage] = useState({});
  const [listBrand, setListBrand] = useState([]);
  const [listCategory, setListCategory] = useState([]);
  const [listMadeIn, setListMadeIn] = useState([]);
  const [listColor, setListColor] = useState([]);
  const [listSize, setListSize] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [loadData, setLoadData] = useState(false);

  const [chillrenColors, setChildrenColors] = useState();
  const {
    register,
    handleSubmit,
    formState: { errors },
    control,
    setValue,
    getValues,
    watch,
  } = useForm({
    defaultValues: initialValues,
    mode: "all",
    // resolver: yupResolver(schema),
  });

  useEffect(() => {
    let listColorIsActive =
      (getValues()?.listProductDetail?.length > 0 &&
        getValues()?.listProductDetail?.map((item) => item.color)) ||
      [];

    if (listColor) {
      const listTemp = [];
      listColor.map((item) => {
        if (!listColorIsActive.includes(item.value)) {
          listTemp.push(item)
        }
      });
      setChildrenColors(listTemp)
    }
  }, [loadData,listColor]);

  useEffect(() => {
    const init = async () => {
      Promise.all([
        brandService.getAll(),
        categoryService.getAll(),
        colorService.getAll(),
        sizeService.getAll(),
        madeinService.getAll(),
      ]).then((res) => {
        //BRAND
        setListBrand(
          res[0]?.data.map((item, index) => {
            return { value: item.id, label: item.name, number: index };
          })
        );
        //CATEGORY
        setListCategory(
          res[1]?.data.map((item, index) => {
            return { value: item.id, label: item.name, number: index };
          })
        );
        //COLOR
        setListColor(
          res[2]?.data.map((item, index) => {
            return { value: item.id, label: item.name, number: index };
          })
        );
        //SIZE
        setListSize(
          res[3]?.data.map((item, index) => {
            return { value: item.id, label: item.name, number: index };
          })
        );

        //MADEIN
