// ** React Imports
import { useEffect, useState } from "react";

// ** Custom Components
import Avatar from "@Components/avatar";

import "@Styles/react/libs/flatpickr/flatpickr.scss";
import { selectThemeColors } from "@Utils";
import Select from "react-select";
import employeeService from "@Services/employeeService";
// ** Third Party Components
import { SCHEMA_CREATE_EMPLOYEE } from "@Constants/constValidate";
import { yupResolver } from "@hookform/resolvers/yup";
import { isObjEmpty } from "@Utils";
import {
  Button,
  Col,
  CustomInput,
  Form,
  FormGroup,
  Input,
  InputGroup,
  Label,
  Media,
  Row,
} from "reactstrap";

import classnames from "classnames";
import { Edit, Trash2 } from "react-feather";
import * as yup from "yup";

import api from "@Constants/api";
import Service from "@Helper/request";
import moment from "moment";
import Flatpickr from "react-flatpickr";
import { Controller, useForm } from "react-hook-form";
import { toast } from "react-toastify";
import { useHistory } from "react-router-dom";

const schema = yup.object(SCHEMA_CREATE_EMPLOYEE).required();

const statusObject = {
  1:"Đang hoạt động",
  0:"Ngừng hoạt động",
}
const roleObject = {
  1:"Quản trị viên",
  0:"Nhân viên",
}
const genderObject = {
  1:"Nam",
  0:"Nữ",
}
const EmployeeTab = ({ initial }) => {
  // ** States
  const [img, setImg] = useState(null);
  const [userData, setUserData] = useState(null);

  const initialValues = {
    ...initial,
    role: roleObject[initial.role],
    status: statusObject[initial.status],
    gender:genderObject[initial.gender],
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
    control,
    setValue,

  } = useForm({
    defaultValues: initialValues,
    resolver: yupResolver(schema),
    mode: "all",
  });
  const history = useHistory();
  // ** Function to change user image
  const onChange = (e) => {
