import "@Styles/react/libs/react-select/_react-select.scss";
import "@Styles/react/libs/tables/react-dataTable-component.scss";
import { Edit, MoreVertical } from "react-feather";
import { Link } from "react-router-dom";
import {
  Badge,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  UncontrolledDropdown,
} from "reactstrap";
const statusBlock = {
  1: {
    text: "Đang hoạt động",
    bgr: "light-success",
  },
  0: {
    text: "Không hoạt động",
    bgr: "light-danger",
  },
};
const columns = [
  {
    name: "id",
    minWidth: "60px",
    selector: (row) => row.id,
    sortable: true,
    sortField: "id",
    cell: (row) => row.id,
  },

  {
    name: "Tên",
    minWidth: "170px",
    selector: (row) => row.name,
    sortable: true,
    sortField: "name",
    cell: (row) => row.name,
  },
  {
    name: "Phần trăm",
    minWidth: "170px",
    selector: (row) => row.name,
    sortable: true,
    sortField: "name",
    cell: (row) => row.name,
  },


  {
    name: "Trạng thái",
    minWidth: "158px",
    selector: (row) => row.status,
    sortable: true,
    sortField: "status",
    // cell: (row) => (
    //   <Badge
    //     className="text-capitalize"
    //     color={statusBlock[row.status].bgr}
    //     pill
    //   >
    //     {statusBlock[row.status].text}
    //   </Badge>
    // ),
  },
  {
    name: "Mô tả",
    minWidth: "170px",
    cell: (row) => row.description,
  },
  {
    name: "Thao tác",
    minWidth: "150px",
    selector: "",
    sortable: true,
    cell: (row) => (
      <div className="column-action d-flex align-items-center">
        <UncontrolledDropdown>
          <DropdownToggle tag="span">
            <MoreVertical size={17} className="cursor-pointer" />
          </DropdownToggle>
          <DropdownMenu right>
            <DropdownItem
              tag={Link}
              to={`/cms/property/color/edit/${row.id}`}
              className="w-100"
            >
              <Edit size={14} className="mr-50" />
              <span className="align-middle">Xem/Sửa</span>
            </DropdownItem>
          </DropdownMenu>
        </UncontrolledDropdown>
      </div>
    ),
  },
];

export default columns;
