// ** React Imports
import { useState } from "react";

// ** Custom Components
import Avatar from "@Components/avatar";

import "@Styles/react/libs/flatpickr/flatpickr.scss";
import { selectThemeColors } from "@Utils";
import Select from "react-select";
import employeeService from "@Services/employeeService";
// ** Third Party Components
import { SCHEMA_CREATE_EMPLOYEE } from "@Constants/constValidate";
import { yupResolver } from "@hookform/resolvers/yup";
import { isObjEmpty } from "@Utils";
import {
  Button,
  Col,
  CustomInput,
  Form,
  FormGroup,
  Input,
  InputGroup,
  Label,
  Media,
  Row,
  Spinner
} from "reactstrap";

import classnames from "classnames";
import { Edit, Trash2 } from "react-feather";
import * as yup from "yup";

import api from "@Constants/api";
import Service from "@Helper/request";
import moment from "moment";
import Flatpickr from "react-flatpickr";
import { Controller, useForm } from "react-hook-form";
import { toast } from "react-toastify";
import { useHistory } from "react-router-dom";

const schema = yup.object(SCHEMA_CREATE_EMPLOYEE).required();
const initialValues = {
  gender: 1,
  role: { value: "0", label: "Nhân viên", number: 2 },
  status: {
    value: "1",
    label: "Đang hoạt động",
    number: 1,
  },
};
const EmployeeTab = ({ selectedUser }) => {
  // ** States
  const [img, setImg] = useState(null);
  const [userData, setUserData] = useState(null);

  const {
    register,
    handleSubmit,
    formState: { errors },
    control,
    setValue,
  } = useForm({
    defaultValues: initialValues,

    resolver: yupResolver(schema),
    mode: "all",
  });
  const history = useHistory();
  const [disable, setDisable] = useState(false);

  // ** Function to change user image
  const onChange = (e) => {
    const reader = new FileReader(),
      files = e.target.files;
    reader.onload = function () {
      setImg(reader.result);
    };
    reader.readAsDataURL(files[0]);
  };
  const onSubmit = async (data) => {
    const customData = {
      ...data,
      birthday: moment(data.birthday).format("DD/MM/YYYY"),
      role: parseInt(data?.role?.value),
      status: parseInt(data?.status?.value),
      password:data?.username
    };
    if (isObjEmpty(errors)) {
      setDisable(true)
      const result = await employeeService.CreateEmployee(customData);
      if (result.isSuccess) {

        toast.success("Thêm nhân viên thành công");
        history.push("/cms/employee/list");
      }
      else{
        setDisable(false)
      }
    }
  };

  // ** Renders User
  const renderUserAvatar = () => {
    if (img === null) {
      const stateNum = Math.floor(Math.random() * 6),
        states = [
          "light-success",
          "light-danger",
          "light-warning",
          "light-info",
          "light-primary",
          "light-secondary",
        ],
        color = states[stateNum];
      return (
        <Avatar
          initials
          color={color}
          className="rounded mr-2 my-25"
          content={" "}
          contentStyles={{
            borderRadius: 0,
            fontSize: "calc(36px)",
            width: "100%",
            height: "100%",
          }}
          style={{
            height: "90px",
            width: "90px",
          }}
        />
      );
    } else {
      return (
        <img
          className="user-avatar rounded mr-2 my-25 cursor-pointer"
          src={img}
          alt="user profile avatar"
          height="90"
          width="90"
        />
      );
    }
  };

  return (
    <Row>
      <Col sm="4">
        <Media className="mb-2">
          {renderUserAvatar()}
          <Media className="mt-50" body>
            <h4>Ảnh </h4>
            <div className="d-flex flex-wrap mt-1 px-0">
              <Button.Ripple
                id="change-img"
                tag={Label}
                className="mr-75 mb-0"
                color="primary"
              >
                <span className="d-none d-sm-block">Tải lên</span>
                <span className="d-block d-sm-none">
                  <Edit size={14} />
                </span>
                <input
                  type="file"
                  hidden
                  id="change-img"
                  onChange={onChange}
                  accept="image/*"
                />
              </Button.Ripple>
              <Button.Ripple color="secondary" outline>
                <span className="d-none d-sm-block">Đặt lại</span>
                <span className="d-block d-sm-none">
                  <Trash2 size={14} />
                </span>
              </Button.Ripple>
            </div>
          </Media>
        </Media>
      </Col>
      <Col sm="12">
        <Form onSubmit={handleSubmit(onSubmit)}>
          <Row>
            <Col sm="4">
              <Label for="nameVerticalIcons">Họ và tên:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="name"
                  className={classnames({ "is-invalid": errors["name"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.name?.message}</p>
            </Col>
            <Col sm="4">
              <Label for="nameVerticalIcons">Tên đăng nhập:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="username"
                  className={classnames({ "is-invalid": errors["username"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.username?.message}</p>
            </Col>

            <Col sm="4">
              <FormGroup>
                <Label className="d-block" for="dob">
                  Ngày sinh
                </Label>
                <Controller
                  name="birthday"
                  as={Flatpickr}
                  control={control}
                  placeholder="DD/MM/YYYY"
                  innerRef={register}
                  options={{
                    dateFormat: "d-m-Y",
                    maxDate: moment(new Date()).format("DD/MM/YYYY"),
                  }}
                  className={classnames("form-control", {
                    "is-invalid": errors["birthday"],
                  })}
                  onChange={(e) => console.log(e)}
                  onFocus={(e) => console.log(e)}
                  // innerRef={register}
                />
                <p className="text-danger">{errors.birthday?.message}</p>
              </FormGroup>
            </Col>
            <Col sm="4">
              <Label>Số điện thoại:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="phone"
                  className={classnames({ "is-invalid": errors["phone"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.phone?.message}</p>
            </Col>

            <Col sm="4">
              <Label className="d-block mb-1">Giới tính:</Label>

              <FormGroup>
                <Controller
                  name="gender"
                  control={control}
                  innerRef={register}
                  render={(props) => {
                    return (
                      <CustomInput
                        inline
                        type="radio"
                        label="Nam"
                        value="1"
                        id="gender-male"
                        defaultChecked
                        name={props.name}
                        onChange={() => setValue("gender", "1")}
                      />
                    );
                  }}
                />
                <Controller
                  name="gender"
                  control={control}
                  innerRef={register}
                  render={(props) => {
                    return (
                      <CustomInput
                        inline
                        type="radio"
                        label="Nữ"
                        value="0"
                        id="gender-female"
                        name={props.name}
                        onChange={() => setValue("gender", "0")}
                      />
                    );
                  }}
                />
              </FormGroup>
            </Col>
            <Col sm="4">
              <Label> Email:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="email"
                  className={classnames({ "is-invalid": errors["email"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.email?.message}</p>
            </Col>
            <Col sm="4">
              <Label> CMND/CCCD:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="identityCard"
                  className={classnames({
                    "is-invalid": errors["identityCard"],
                  })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.identityCard?.message}</p>
            </Col>
            <Col sm="4">
              <Label> Vai trò:</Label>
              <Controller
                control={control}
                name="role"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Quản trị viên", number: 1 },
                      { value: "0", label: "Nhân viên", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>
            <Col sm="4">
              <Label>Trạng thái:</Label>
              <Controller
                control={control}
                name="status"
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      { value: "1", label: "Đang hoạt động", number: 1 },
                      { value: "0", label: "Ngừng hoạt động", number: 2 },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>

            <Col sm="12">
              <Label> Ghi chú:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name="note"
                  type="textarea"
                  className={classnames({ "is-invalid": errors["note"] })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">{errors.note?.message}</p>
            </Col>
          </Row>
          <Row className="justify-content-end">
            {/* <Col sm="4"> */}
            <FormGroup className="d-flex mb-0 mx-1">
              <Button.Ripple className="mr-1" color="primary" type="submit"
                disabled={disable}
                >

                {disable ? (
                  <>
                    <Spinner size="sm" />
                    <span className="ml-50">Vui lòng đợi...</span>
                  </>
                ) : (
                  "Thêm"
                )}
              </Button.Ripple>
              <Button.Ripple outline color="secondary" onClick={()=>{
                history.push("/cms/employee/list")
              }}>
                Huỷ
              </Button.Ripple>
            </FormGroup>
            {/* </Col> */}
          </Row>
        </Form>
      </Col>
    </Row>
  );
};
export default EmployeeTab;
