import { useFieldArray } from "react-hook-form";
// ** React Imports

// ** Custom Components

import "@Styles/react/libs/flatpickr/flatpickr.scss";
import { selectThemeColors } from "@Utils";
import Select from "react-select";
// ** Third Party Components
import {
  Button,
  Col,
  FormGroup,
  Input,
  InputGroup,
  Label,
  Row,
} from "reactstrap";

import classnames from "classnames";
import { Minus, Plus } from "react-feather";

import { Controller } from "react-hook-form";
const SizeAndAmount = ({ nestIndex, control, register, errors }) => {
  const { fields, remove, append } = useFieldArray({
    control,
    name: `listProductDetail.${nestIndex}.sizeAndAmount`,
  });

  return (
    <div>
      {fields.map((item, k) => {
        return (
          <Row key={item.id}>
            <Col sm="5">
              <Label>Kích cỡ:</Label>
              <Controller
                control={control}
                name={`listProductDetail.${nestIndex}.sizeAndAmount.${k}.size`}
                render={({ onChange, value }) => (
                  <Select
                    theme={selectThemeColors}
                    isClearable={false}
                    className="react-select"
                    classNamePrefix="select"
                    options={[
                      {
                        value: "1",
                        label: "Đang hoạt động",
                        number: 1,
                      },
                      {
                        value: "0",
                        label: "Ngừng hoạt động",
                        number: 2,
                      },
                    ]}
                    value={value}
                    innerRef={register}
                    onChange={onChange}
                  />
                )}
              />
            </Col>
            <Col sm="5">
              <Label for="nameVerticalIcons">Số lượng tồn:</Label>
              <InputGroup className="input-group-merge" tag={FormGroup}>
                <Input
                  name={`listProductDetail.${nestIndex}.sizeAndAmount.${k}.amount`}
                  className={classnames({
                    "is-invalid":
                      errors[
                        `listProductDetail.${nestIndex}.sizeAndAmount.${k}.amount`
                      ],
                  })}
                  innerRef={register}
                />
              </InputGroup>
              <p className="text-danger">
                {
                  errors[
                    `listProductDetail.${nestIndex}.sizeAndAmount.${k}.amount`
                  ]?.message
                }
              </p>
            </Col>
            <Button.Ripple
              className="btn-icon m-0 button-minus mt-2"
              color="danger"
              outline
              onClick={() => {
                remove(k);
              }}
              tag={Label}
            >
              <Minus size={20} />
            </Button.Ripple>
          </Row>

          //   <div key={item.id} style={{ marginLeft: 20 }}>
          //     <label>Nested Array:</label>
          //     <input
          //       {...register(`test.${nestIndex}.nestedArray.${k}.field1`, {
          //         required: true
          //       })}
          //       style={{ marginRight: "25px" }}
          //     />

          //     <input {...register(`test.${nestIndex}.nestedArray.${k}.field2`)} />
          //     <button type="button" onClick={() => remove(k)}>
          //       Delete Nested
          //     </button>
          //   </div>
        );
      })}
      <Button.Ripple
        className="btn-icon m-0 "
        color="info"
        id="image"
        outline
        onClick={() =>
          append({
            size: null,
            amount: null,
          })
        }
        tag={Label}
      >
        <Plus size={20} />
      </Button.Ripple>

      <hr />
    </div>
  );
};

export default SizeAndAmount;

